
    // Function to change the class on mouseover and mouseout

    function changeClass(element) {
        element.classList.add('ha_color_red');
    }

    function removeClass(element){
        element.classList.remove('ha_color_red');
    }

// для иконок youtube watsap и тд
    function removeClassSocial(element) {
        element.classList.remove('ha_opasity');
    }
    
    
    function changeClassSocial(element){
        element.classList.add('ha_opasity');
    }
    
    
    // цены 
    function changeClassPrice(element) {
        element.classList.add('ha_price_color');
    }
    
    function removeClassPrice(element){
        element.classList.remove('ha_price_color');
    }
